import React, { useState, useEffect } from "react";
import firebase from "firebase";
import "./App.css";
import TodoList from "./components/todoList";
import database from "./firebase_init";
import { Button, TextField } from "@material-ui/core";
import LocalFireDepartmentIcon from '@mui/icons-material/LocalFireDepartment';


function App() {
  // state managment
  const [names, setNames] = useState([]);
  const [input_name, setInput_name] = useState("");


  // hook to det data from database
  useEffect(() => {
    database
      .collection("immoral")
      .onSnapshot((Snapshot) => {
        setNames(
          Snapshot.docs.map((todo) => ({ id: todo.id, name: todo.data().name }))
        );
      });
  }, []);

  // function to add todo to our database
  const Addtodo = (event) => {
    event.preventDefault();
    if (input_name !== "") {
      database.collection("immoral").add({
        name: input_name,
        timeStamp: firebase.firestore.FieldValue.serverTimestamp(),
      });
    }
    setInput_name("");
  };

  return (
    <div className="App">
      <form className="getinput">

        <Button 
          className="form-btn"
          variant="outlined"
          type="submit"
          color="primary"
          size="large"
          onClick={Addtodo}
          startIcon={<LocalFireDepartmentIcon />}
        >
הדלק נר        </Button>
        <TextField
          className="todo-input"
          id="standard-basic"
          label="שם"
          type="text"
          value={input_name}
          onChange={(event) => setInput_name(event.target.value)}
        />
      </form>
      <h3 className="heading">שמות הנופלים</h3>
      <div className="container_1">
        {names.map((name) => (
          <TodoList text={name} />
        ))}
      </div>
    </div>
  );
}

export default App;
