import React, { useState, useEffect } from "react";
import firebase from "firebase";
import "./App.css";
//import TodoList from "./components/todoList";
import database from "./firebase_init";
import { Button, TextField } from "@material-ui/core";
import LocalFireDepartmentIcon from '@mui/icons-material/LocalFireDepartment';
import MenuItem from '@mui/material/MenuItem';
const currencies = [
  {
    value: 'red',
    label: 'שיאון',
  },
  {
    value: 'yew',
    label: 'דלתון',
  },
  {
    value: 'gre',
    label: 'חרמון',
  },
  {
    value: 'bw',
    label: 'קדרון',
  },
];

function App() {
  // state managment
  const [names, setNames] = useState([]);
  const [input_name, setInput_name] = useState("");
  const [currency, setCurrency] = useState('red');

  const handleChange = (event) => {
    setCurrency(event.target.value);
  };


  // hook to det data from database
  useEffect(() => {
    database
      .collection("memorial")
      .onSnapshot((Snapshot) => {
        setNames(
          Snapshot.docs.map((todo) => ({ id: todo.id, name: todo.data().name, ploga: todo.data().ploga }))
        );
      });
  }, []);

  // function to add todo to our database
  const Addtodo = (event) => {
    event.preventDefault();
    if (input_name !== "") {
      database.collection("memorial").add({
        name: input_name,
        ploga:currency,
        timeStamp: firebase.firestore.FieldValue.serverTimestamp(),
      });
    }
    setInput_name("");
  };

  return (
    <div class ="bg">
        <LoginForm />

      <form className="getinput">
        <Button 
          className="form-btn"
          variant="outlined"
          type="submit"
          color="primary"
          size="large"
          onClick={Addtodo}
          startIcon={<LocalFireDepartmentIcon />}
        >
הדלק נר        </Button>
<TextField
          id="outlined-select-currency"
          select
          label="פלוגה"
          value={currency}
          onChange={handleChange}
        >
          {currencies.map((option) => (
            <MenuItem key={option.value} value={option.value}>
              {option.label}
            </MenuItem>
          ))}
        </TextField>
        <TextField
          className="todo-input"
          id="standard-basic"
          label="שם"
          type="text"
          value={input_name}
          onChange={(event) => setInput_name(event.target.value)}
        />
      </form>
      <h3 className="heading">יזכור</h3>
    </div>
  );
}



class LoginForm extends React.Component{
render(){
  return(
    <div id="loginform">
      <FormHeader title="Login" />
      <Form />
      <OtherMethods />
    </div>
  )
}
}

const FormHeader = props => (
  <h2 id="headerTitle">{props.title}</h2>
);


const Form = props => (
 <div>
   <FormInput description="Username" placeholder="Enter your username" type="text" />
   <FormInput description="Password" placeholder="Enter your password" type="password"/>
   <FormButton title="Log in"/>
 </div>
);

const FormButton = props => (
<div id="button" class="row">
  <button>{props.title}</button>
</div>
);

const FormInput = props => (
<div class="row">
  <label>{props.description}</label>
  <input type={props.type} placeholder={props.placeholder}/>
</div>  
);

const OtherMethods = props => (
<div id="alternativeLogin">
  <label>Or sign in with:</label>
  <div id="iconGroup">
    <Facebook />
    <Twitter />
    <Google />
  </div>
</div>
);
