import * as React from 'react';
import { useState, useEffect } from "react";
import Avatar from '@mui/material/Avatar';
import Button from '@mui/material/Button';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Link from '@mui/material/Link';
import Grid from '@mui/material/Grid';

import Box from '@mui/material/Box';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import LocalFireDepartmentIcon from '@mui/icons-material/LocalFireDepartment';
import MenuItem from '@mui/material/MenuItem';
import firebase from "firebase";
import database from "./firebase_init";
import "./login.css";
import {
    useTheme,
    createMuiTheme,
    MuiThemeProvider
  } from "@material-ui/core/styles";



const currencies = [
    {
      value: 'red',
      label: 'שיאון',
    },
    {
      value: 'yew',
      label: 'דלתון',
    },
    {
      value: 'gre',
      label: 'חרמון',
    },
    {
      value: 'bw',
      label: 'קדרון',
    },
  ];

function Copyright(props) {
  return (
    <Typography variant="body2" color="text.secondary" align="center" {...props}>
      {'Copyright © '}
      <Link color="inherit" href="https://AMANSTART.Herukoapp.com/">
        AMANSTART
      </Link>{' '}
      {new Date().getFullYear()}
      {'.'}
    </Typography>
  );
}

const theme = createTheme();

export default function Login() {
  const handleSubmit = (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);
    // eslint-disable-next-line no-console
    console.log({
      email: data.get('email'),
      password: data.get('password'),
    });
  };
  const [names, setNames] = useState([]);
  const [input_name, setInput_name] = useState("");
  const [currency, setCurrency] = useState('red');

  const theme = createMuiTheme({
    typography: {
      fontFamily: [
        'Heebo',
      ].join(','),
    },
  });
  const handleChange = (event) => {
    setCurrency(event.target.value);
  };

  // hook to det data from database
  useEffect(() => {
    database
      .collection("memorial")
      .onSnapshot((Snapshot) => {
        setNames(
          Snapshot.docs.map((todo) => ({ id: todo.id, name: todo.data().name, ploga: todo.data().ploga }))
        );
      });
  }, []);

  // function to add todo to our database
  const Addtodo = (event) => {


    if (input_name !== "") {
      database.collection("memorial").add({
        name: input_name,
        ploga:currency,
        timeStamp: firebase.firestore.FieldValue.serverTimestamp(),
      });
    }
    setInput_name("");
  };

  return (
    <ThemeProvider theme={theme}>
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
                      <Avatar sx={{ m: 1, bgcolor: 'secondary.main' }}>
            <LocalFireDepartmentIcon />
          </Avatar>
                     <Typography style={{ fontSize: 30 }} dir="rtl" component="h1" variant="h5">
                         הדלקת נר זכרון במסע קמ"נים
        </Typography>
        <Typography style={{ fontWeight: 600 }} dir="rtl" component="h1" variant="h5">
לזכרי חללי מערכות ישראל        </Typography>


          <Box component="form">
            <TextField             style={{
                    backgroundColor: "white",
                }}
            dir="rtl"
              margin="שם החלל"
                            fullWidth
              id="name"
              label="שם החלל"
              name=""
              autoFocus
              type="text"
              value={input_name}
              onChange={(event) => setInput_name(event.target.value)}
            />
          <TextField                 style={{
                    backgroundColor: "white",
                }}
          dir="rtl"
                        margin="normal"
                        required
                        fullWidth
                        name="פלוגה"
                        label="פלוגה"
          id="outlined-select-currency"
          select
          value={currency}
          onChange={handleChange}
        >
          {currencies.map((option) => (
            <MenuItem style={{ color: 'white' }} key={option.value} value={option.value}>
              {option.label}
            </MenuItem>
          ))}
        </TextField>

            <Button 
                style={{
                    backgroundColor: "white",
                    fontWeight: 600
                }}
                type="submit"
                fullWidth
                variant="contained"
                sx={{ mt: 3, mb: 2 }}
          variant="outlined"
          type="submit"
          color="primary"
          size="large"
          onClick={Addtodo}
          startIcon={<LocalFireDepartmentIcon />}
        >
הדלק נר        </Button>

          </Box>
        </Box>
        <Grid        
      spacing={2}

>
<Typography dir="rtl" component="h1" variant="h5">
רשימת החללים:        </Typography>
<div class="con" dir="rtl">
<li><a href ='http://www.google.com'>יום רביעי: מלחמת יום כיפור </a></li>
<li><a href ='http://www.google.com'>יום חמישי: אסון המסוקים</a></li>
<li><a href ='http://www.google.com'>יום שישי: מלחמת העצמאות</a></li>
<li><a href ='http://www.google.com'>יום שבת: חללי אמ"ן</a></li>
<li><a href ='http://www.google.com'>יום ראשון: מלחמת ששת הימים</a></li>
<li><a href ='http://www.google.com'>יום שני: צוק איתן</a></li>
  </div>
        </Grid>
        <Copyright sx={{ mt: 8, mb: 4 }} />

      </Container>
    </ThemeProvider>
  );
}
