import React, { useState, useEffect } from "react";
import firebase from "firebase";
import "./App.css";
//import TodoList from "./components/todoList";
import database from "./firebase_init";
import { Button, TextField } from "@material-ui/core";
import LocalFireDepartmentIcon from '@mui/icons-material/LocalFireDepartment';
import MenuItem from '@mui/material/MenuItem';
const currencies = [
  {
    value: 'card_red',
    label: 'שיאון',
  },
  {
    value: 'card_yew',
    label: 'דלתון',
  },
  {
    value: 'card_gre',
    label: 'חרמון',
  },
  {
    value: 'card_bw',
    label: 'קדרון',
  },
];

function App() {
  // state managment
  const [names, setNames] = useState([]);
  const [input_name, setInput_name] = useState("");
  const [currency, setCurrency] = useState('card_red');

  const handleChange = (event) => {
    setCurrency(event.target.value);
  };


  // hook to det data from database
  useEffect(() => {
    database
      .collection("memorial")
      .onSnapshot((Snapshot) => {
        setNames(
          Snapshot.docs.map((todo) => ({ id: todo.id, name: todo.data().name }))
        );
      });
  }, []);

  // function to add todo to our database
  const Addtodo = (event) => {
    event.preventDefault();
    if (input_name !== "") {
      database.collection("memorial").add({
        name: input_name,
        ploga:currency,
        timeStamp: firebase.firestore.FieldValue.serverTimestamp(),
      });
    }
    setInput_name("");
  };

  return (
    <div >
             <TextField
          id="outlined-select-currency"
          select
          label="פלוגה"
          value={currency}
          onChange={handleChange}
        >
          {currencies.map((option) => (
            <MenuItem key={option.value} value={option.value}>
              {option.label}
            </MenuItem>
          ))}
        </TextField>
      <form className="getinput">
        <Button 
          className="form-btn"
          variant="outlined"
          type="submit"
          color="primary"
          size="large"
          onClick={Addtodo}
          startIcon={<LocalFireDepartmentIcon />}
        >
הדלק נר        </Button>
        <TextField
          className="todo-input"
          id="standard-basic"
          label="שם"
          type="text"
          value={input_name}
          onChange={(event) => setInput_name(event.target.value)}
        />
      </form>
      <h3 className="heading">שמות הנופלים</h3>
      <div className="container">
        {names.map((name) => (
      <div className="card_red"  >
      <div class="overlay">        
        <div class="center">
        {name.name}
        </div>
      </div>
    </div>
            ))}
      </div>
    </div>
  );
}

export default App;
